mapper.select:
select 'false' as QUERYID,

from s_user m
where m.user_id = #{id,jdbcType=INTEGER}

mapper.update:
update s_user
set user_name = #{name,jdbcType=VARCHAR},
  user_birthday = #{birthday,jdbcType=DATE},
  user_salary = #{salary,jdbcType=DOUBLE}
where user_id = #{id,jdbcType=INTEGER}
